import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link, Routes, useParams } from 'react-router-dom';
import axios from 'axios';
import submitForm from './helpers/submitForm';

function App() {
  const [companies, setCompanies] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCompanies = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/companies');
        setCompanies(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching companies:', error);
        setLoading(false);
      }
    };

    fetchCompanies();
  }, []);

  if (loading) {
    return <div>Cargando...</div>;
  }

  return (
    <Router>
      <div>
        <header>
          <h1>Selecciona una empresa</h1>
          <nav>
            {companies.map(company => (
              <Link key={company.id} to={`/${company.slug}`}>
                <button>{company.name}</button>
              </Link>
            ))}
          </nav>
        </header>

        <Routes>
          <Route path="/:slug" element={<CompanyRoutes companies={companies} />} />
        </Routes>
      </div>
    </Router>
  );
}

function CompanyRoutes(props) {
  const { slug } = useParams();
  const { companies } = props;
  const company = companies.find(c => c.slug === slug);

  if (!company) {
    return <div>Cargando...</div>;
  }

  return (
    <div>
      <h1>{company.name}</h1>
      <Routes>
        <Route path="/" element={<UserList company={company} />} />
        <Route path="/registrar" element={<RegistrationForm company={company} submitForm={submitForm} />} />
      </Routes>
    </div>
  );
}

function UserList(props) {
  const { company } = props;
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const fetchUsers = async () => {
      try {
        if (company) {
          const response = await axios.get(`http://localhost:5000/api/users?company=${company.slug}&page=1&limit=10`);
          setUsers(response.data);
        }
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchUsers();
  }, [company]);

  return (
    <div>
      <h2>Usuarios</h2>
      <ul>
        {users.map(user => (
          <li key={user.id}>
            {user.name} {user.lastname} - {user.email}
          </li>
        ))}
      </ul>
      <Link to={`/${company.slug}/registrar`}>
        <button>Registrar</button>
      </Link>
    </div>
  );
}

function RegistrationForm(props) {
  const { company, submitForm } = props;
  const [formData, setFormData] = useState({
    name: '',
    lastname: '',
    dni: '',
    email: '',
    password: '',
    company_id: company.id,
  });
  const [formErrors, setFormErrors] = useState({});

  const handleInputChange = (event) => {
    setFormData({ ...formData, [event.target.name]: event.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await submitForm(formData);
      alert('Usuario registrado exitosamente en la compañía: ' + company.name);
    } catch (error) {
      console.error('Error submitting form:', error);
      setFormErrors({ general: error.message });
    }
  };

  return (
    <div>
      <h2>Registro de usuario</h2>
      <div>
        <form onSubmit={handleSubmit}>
          <div>
            <label htmlFor="name">Nombre<span>*</span></label>
            <input type="text" name="name" id="name" value={formData.name} onChange={handleInputChange} />
            {formErrors.name && <span>{formErrors.name}</span>}
          </div>
          {/* ... resto del formulario sin cambios */}
          {formErrors.general && <span>{formErrors.general}</span>}
          <button type="submit">Registrar</button>
        </form>
      </div>
    </div>
  );
}

export default App;
