import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link, useLocation, useParams } from 'react-router-dom';
import './App.css';
import axios from 'axios';

function App() {
  const [companies, setCompanies] = useState([]);

  useEffect(() => {
    const fetchCompanies = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/companies');
        setCompanies(response.data);
      } catch (error) {
        console.error('Error fetching companies:', error);
      }
    };

    fetchCompanies();
  }, []);

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <Route exact path="/" component={Home} />
          <Route path="/:subdomain" component={CompanyRoutes} />
        </header>
      </div>
    </Router>
  );

  function Home() {
    return (
      <>
        {companies.map(company => (
          <Link key={company.id} to={`/${company.subdomain}`}>
            <button className="company-btn">{company.name}</button>
          </Link>
        ))}
      </>
    );
  }

  function CompanyRoutes() {
    const { subdomain } = useParams();

    return (
      <>
        <Route exact path={`/${subdomain}`} component={UserList} />
        <Route path={`/${subdomain}/registro`} component={RegistrationForm} />
      </>
    );
  }

  function UserList() {
    const [users, setUsers] = useState([]);
    const location = useLocation();
    const subdomain = location.pathname.split('/')[1];

    useEffect(() => {
      const fetchUsers = async () => {
        try {
          const response = await axios.get(`http://localhost:5000/api/users?company=${subdomain}&page=1&limit=10`);
          setUsers(response.data);
        } catch (error) {
          console.error('Error fetching users:', error);
        }
      };

      fetchUsers();
    }, [subdomain]);

    return (
      <>
        <h1>{subdomain}</h1>
        <Link to={`/${subdomain}/registro`}>
          <button>Registro</button>
        </Link>
        <ul>
          {users.map(user => (
            <li key={user.id}>
              {user.name} {user.lastname} - {user.email}
            </li>
          ))}
        </ul>
      </>
    );
  }
}

function RegistrationForm() {
  const [formData, setFormData] = useState({
    name: '',
    lastname: '',
    dni: '',
    email: '',
    password: '',
    company: '',
  });
  const [formErrors, setFormErrors] = useState({});
  const location = useLocation();
  const subdomain = location.pathname.split('/')[1];

  const handleInputChange = (event) => {
    setFormData({ ...formData, [event.target.name]: event.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const company = companies.find(c => c.subdomain === subdomain);
    const errors = validateForm(formData);
    if (!company) errors.company = 'Invalid company selected.';
    if (Object.keys(errors).length === 0) {
      try {
        await axios.post('http://localhost:5000/api/user/register', {
          ...formData,
          company_id: company.id
        });
        alert('Usuario registrado exitosamente en la compañía: ' + company.name);
      } catch (error) {
        console.error('Error submitting form:', error);
      }
    } else {
      setFormErrors(errors);
    }
  };

  const validateForm = (data) => {
    const errors = {};
    if (!data.name.trim()) {
      errors.name = 'El nombre es obligatorio';
    }
    if (!data.lastname.trim()) {
      errors.lastname = 'Los apellidos son obligatorios';
    }
    if (!data.dni.trim()) {
      errors.dni = 'El DNI es obligatorio';
    } else if (!/^\d{8}$/.test(data.dni)) {
      errors.dni = 'El DNI debe ser un número de 8 dígitos';
    }
    if (!data.email.trim()) {
      errors.email = 'El correo electrónico es obligatorio';
    } else if (!/\S+@\S+\.\S+/.test(data.email)) {
      errors.email = 'El correo electrónico no es válido';
    }
    if (!data.password.trim()) {
      errors.password = 'La contraseña es obligatoria';
    }
    return errors;
  };

  return (
    <>
      <h1>{subdomain}</h1>
      <div className="form-container">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Nombre<span>*</span></label>
            <input type="text" name="name" id="name" value={formData.name} onChange={handleInputChange} />
            {formErrors.name && <span className="error-message">{formErrors.name}</span>}
          </div>
          <div className="form-group">
            <label htmlFor="lastname">Apellidos<span>*</span></label>
            <input type="text" name="lastname" id="lastname" value={formData.lastname} onChange={handleInputChange} />
            {formErrors.lastname && <span className="error-message">{formErrors.lastname}</span>}
          </div>
          <div className="form-group">
            <label htmlFor="dni">DNI<span>*</span></label>
            <input type="text" name="dni" id="dni" value={formData.dni} onChange={handleInputChange} />
            {formErrors.dni && <span className="error-message">{formErrors.dni}</span>}
          </div>
          <div className="form-group">
            <label htmlFor="email">Correo Electrónico<span>*</span></label>
            <input type="email" name="email" id="email" value={formData.email} onChange={handleInputChange} />
            {formErrors.email && <span className="error-message">{formErrors.email}</span>}
          </div>
          <div className="form-group">
            <label htmlFor="password">Contraseña<span>*</span></label>
            <input type="password" name="password" id="password" value={formData.password} onChange={handleInputChange} />
            {formErrors.password && <span className="error-message">{formErrors.password}</span>}
          </div>
          <button type="submit">Registrar</button>
        </form>
      </div>
    </>
  );
}

export default App;
