import React, { useState } from 'react';
import './App.css';
import submitForm from './helpers/submitForm';

function App() {
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    lastName: '',
    dni: '',
    email: '',
    password: '',
    company: '',
  });
  const [formErrors, setFormErrors] = useState({});

  const handleCompanyClick = (company) => {
    setFormData({ ...formData, company });
    setShowForm(true);
  };

  const handleInputChange = (event) => {
    setFormData({ ...formData, [event.target.name]: event.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const errors = validateForm(formData);
    if (Object.keys(errors).length === 0) {
      const success = await submitForm(formData);
      if (success) {
        setTimeout(() => {
          window.location.href = `http://${formData.company}.localhost:3001`;
        }, 100); // Slight delay to ensure state update
      }
    } else {
      setFormErrors(errors);
    }
  };

  const validateForm = (data) => {
    const errors = {};
    if (!data.name.trim()) {
      errors.name = 'El nombre es obligatorio';
    }
    if (!data.lastName.trim()) {
      errors.lastName = 'Los apellidos son obligatorios';
    }
    if (!data.dni.trim()) {
      errors.dni = 'El DNI es obligatorio';
    } else if (!/^\d{8}$/.test(data.dni)) {
      errors.dni = 'El DNI debe ser un número de 8 dígitos';
    }
    if (!data.email.trim()) {
      errors.email = 'El correo electrónico es obligatorio';
    } else if (!/\S+@\S+\.\S+/.test(data.email)) {
      errors.email = 'El correo electrónico no es válido';
    }
    if (!data.password.trim()) {
      errors.password = 'La contraseña es obligatoria';
    }
    return errors;
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="companies">
          <button className="company-btn" onClick={() => handleCompanyClick('kallpa')}>kallpa</button>
          <button className="company-btn" onClick={() => handleCompanyClick('energy')}>energy</button>
        </div>
        {showForm && (
          <div className="form-container">
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="name">Nombre<span>*</span></label>
                <input
                  type="text"
                  name="name"
                  id="name"
                  value={formData.name}
                  onChange={handleInputChange}
                />
                {formErrors.name && <span className="error-message">{formErrors.name}</span>}
              </div>
              <div className="form-group">
                <label htmlFor="lastName">Apellidos<span>*</span></label>
                <input
                  type="text"
                  name="lastName"
                  id="lastName"
                  value={formData.lastName}
                  onChange={handleInputChange}
                />
                {formErrors.lastName && <span className="error-message">{formErrors.lastName}</span>}
              </div>
              <div className="form-group">
                <label htmlFor="dni">DNI<span>*</span></label>
                <input
                  type="text"
                  name="dni"
                  id="dni"
                  value={formData.dni}
                  onChange={handleInputChange}
                />
                {formErrors.dni && <span className="error-message">{formErrors.dni}</span>}
              </div>
              <div className="form-group">
                <label htmlFor="email">Correo Electrónico<span>*</span></label>
                <input
                  type="email"
                  name="email"
                  id="email"
                  value={formData.email}
                  onChange={handleInputChange}
                />
                {formErrors.email && <span className="error-message">{formErrors.email}</span>}
              </div>
              <div className="form-group">
                <label htmlFor="password">Contraseña<span>*</span></label>
                <input
                  type="password"
                  name="password"
                  id="password"
                  value={formData.password}
                  onChange={handleInputChange}
                />
                {formErrors.password && <span className="error-message">{formErrors.password}</span>}
              </div>
              <button type="submit">Registrar</button>
            </form>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
