import React, { useState, useEffect } from 'react';
import './App.css';
import axios from 'axios';

function App() {
  const [showForm, setShowForm] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    lastName: '',
    dni: '',
    email: '',
    password: '',
    company: '',
  });
  const [formErrors, setFormErrors] = useState({});
  const [users, setUsers] = useState([]);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [company, setCompany] = useState('');

  useEffect(() => {
    const companyFromURL = window.location.hostname.split('.')[0];
    if (companyFromURL !== 'localhost') {
      setCompany(companyFromURL);
      fetchUsers(companyFromURL, page);
    }
  }, [page]);

  const fetchUsers = async (company, page) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/users`, {
        params: {
          company,
          page,
          limit: 10,
        },
      });
      setUsers(response.data.users);
      setTotalPages(response.data.totalPages);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const handleCompanyClick = (company) => {
    setFormData({ ...formData, company });
    setCompany(company);
    window.location.href = `http://${company}.localhost:3001`;
  };

  const handleInputChange = (event) => {
    setFormData({ ...formData, [event.target.name]: event.target.value });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const errors = validateForm(formData);
    if (Object.keys(errors).length === 0) {
      try {
        await axios.post('http://localhost:5000/api/user/register', formData);
        window.location.href = `http://${formData.company}.localhost:3001`;
      } catch (error) {
        console.error('Error submitting form:', error);
      }
    } else {
      setFormErrors(errors);
    }
  };

  const validateForm = (data) => {
    const errors = {};
    if (!data.name.trim()) {
      errors.name = 'El nombre es obligatorio';
    }
    if (!data.lastName.trim()) {
      errors.lastName = 'Los apellidos son obligatorios';
    }
    if (!data.dni.trim()) {
      errors.dni = 'El DNI es obligatorio';
    } else if (!/^\d{8}$/.test(data.dni)) {
      errors.dni = 'El DNI debe ser un número de 8 dígitos';
    }
    if (!data.email.trim()) {
      errors.email = 'El correo electrónico es obligatorio';
    } else if (!/\S+@\S+\.\S+/.test(data.email)) {
      errors.email = 'El correo electrónico no es válido';
    }
    if (!data.password.trim()) {
      errors.password = 'La contraseña es obligatoria';
    }
    return errors;
  };

  const handleRegisterClick = () => {
    setShowForm(true);
    window.history.pushState({}, '', '/registro');
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="companies">
          <button className="company-btn" onClick={() => handleCompanyClick('kallpa')}>kallpa</button>
          <button className="company-btn" onClick={() => handleCompanyClick('energy')}>energy</button>
        </div>
        {!showForm ? (
          <div>
            <h2>Usuarios de {company}</h2>
            <ul>
              {users.map(user => (
                <li key={user.id}>{user.name} {user.lastname}</li>
              ))}
            </ul>
            <div>
              {Array.from({ length: totalPages }, (_, i) => (
                <button key={i} onClick={() => setPage(i + 1)} disabled={page === i + 1}>
                  {i + 1}
                </button>
              ))}
            </div>
          </div>
        ) : (
          <div className="form-container">
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="name">Nombre<span>*</span></label>
                <input
                  type="text"
                  name="name"
                  id="name"
                  value={formData.name}
                  onChange={handleInputChange}
                />
                {formErrors.name && <span className="error-message">{formErrors.name}</span>}
              </div>
              <div className="form-group">
                <label htmlFor="lastName">Apellidos<span>*</span></label>
                <input
                  type="text"
                  name="lastName"
                  id="lastName"
                  value={formData.lastName}
                  onChange={handleInputChange}
                />
                {formErrors.lastName && <span className="error-message">{formErrors.lastName}</span>}
              </div>
              <div className="form-group">
                <label htmlFor="dni">DNI<span>*</span></label>
                <input
                  type="text"
                  name="dni"
                  id="dni"
                  value={formData.dni}
                  onChange={handleInputChange}
                />
                {formErrors.dni && <span className="error-message">{formErrors.dni}</span>}
              </div>
              <div className="form-group">
                <label htmlFor="email">Correo Electrónico<span>*</span></label>
                <input
                  type="email"
                  name="email"
                  id="email"
                  value={formData.email}
                  onChange={handleInputChange}
                />
                {formErrors.email && <span className="error-message">{formErrors.email}</span>}
              </div>
              <div className="form-group">
                <label htmlFor="password">Contraseña<span>*</span></label>
                <input
                  type="password"
                  name="password"
                  id="password"
                  value={formData.password}
                  onChange={handleInputChange}
                />
                {formErrors.password && <span className="error-message">{formErrors.password}</span>}
              </div>
              <button type="submit">Registrar</button>
            </form>
          </div>
        )}
        {!showForm && <button onClick={handleRegisterClick}>Registro</button>}
      </header>
    </div>
  );
}

export default App;
