// UserForm.js
import React, { useState } from 'react';
import axios from 'axios';
import './UserForm.css';

function UserForm({ company }) {
    const [formData, setFormData] = useState({
        name: '',
        lastname: '',
        dni: '',
        email: '',
        password: '',
        company: company,
    });
    const [formErrors, setFormErrors] = useState({});

    const handleInputChange = (event) => {
        setFormData({ ...formData, [event.target.name]: event.target.value });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const errors = validateForm(formData);
        if (Object.keys(errors).length === 0) {
            try {
                await axios.post('http://localhost:5000/api/user/register', formData);
                alert('Usuario registrado exitosamente en la compañía: ' + formData.company);
            } catch (error) {
                console.error('Error submitting form:', error);
            }
        } else {
            setFormErrors(errors);
        }
    };

    const validateForm = (data) => {
        const errors = {};
        if (!data.name.trim()) {
            errors.name = 'El nombre es obligatorio';
        }
        if (!data.lastname.trim()) {
            errors.lastname = 'Los apellidos son obligatorios';
        }
        if (!data.dni.trim()) {
            errors.dni = 'El DNI es obligatorio';
        } else if (!/^\d{8}$/.test(data.dni)) {
            errors.dni = 'El DNI debe ser un número de 8 dígitos';
        }
        if (!data.email.trim()) {
            errors.email = 'El correo electrónico es obligatorio';
        } else if (!/\S+@\S+\.\S+/.test(data.email)) {
            errors.email = 'El correo electrónico no es válido';
        }
        if (!data.password.trim()) {
            errors.password = 'La contraseña es obligatoria';
        }
        return errors;
    };

    return (
        <div className="form-container">
            <h2>Registro de Usuario - {company}</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input
                        type="text"
                        id="name"
                        name="name"
                        placeholder="Nombre"
                        value={formData.name}
                        onChange={handleInputChange}
                    />
                    {formErrors.name && <span className="error">{formErrors.name}</span>}
                </div>
                <div className="form-group">
                    <input
                        type="text"
                        id="lastname"
                        name="lastname"
                        placeholder="Apellidos"
                        value={formData.lastname}
                        onChange={handleInputChange}
                    />
                    {formErrors.lastname && <span className="error">{formErrors.lastname}</span>}
                </div>
                <div className="form-group">
                    <input
                        type="text"
                        id="dni"
                        name="dni"
                        placeholder="DNI"
                        value={formData.dni}
                        onChange={handleInputChange}
                    />
                    {formErrors.dni && <span className="error">{formErrors.dni}</span>}
                </div>
                <div className="form-group">
                    <input
                        type="email"
                        id="email"
                        name="email"
                        placeholder="Correo Electrónico"
                        value={formData.email}
                        onChange={handleInputChange}
                    />
                    {formErrors.email && <span className="error">{formErrors.email}</span>}
                </div>
                <div className="form-group">
                    <input
                        type="password"
                        id="password"
                        name="password"
                        placeholder="Contraseña"
                        value={formData.password}
                        onChange={handleInputChange}
                    />
                    {formErrors.password && <span className="error">{formErrors.password}</span>}
                </div>
                <button type="submit">Registrar</button>
            </form>
        </div>
    );
}

export default UserForm;
